import dayjs from 'dayjs';
export default {
  name: 'ToolConsult',
  props: ['visible'],
  data: function data() {
    return {
      sendContent: '',
      messages: [{
        type: 'send',
        from: '我',
        // TODO: 改为准考证号
        time: '2022-04-11 20:44:12',
        content: '你好'
      }, {
        type: 'receive',
        from: '监考老师',
        time: '2022-04-11 20:46:16',
        content: '好的'
      }]
    };
  },
  watch: {
    messages: {
      handler: function handler() {
        this.updateScroll();
      }
    }
  },
  methods: {
    closeTool: function closeTool() {
      this.$emit('changeVisible', false);
    },
    updateScroll: function updateScroll() {
      var chatBox = this.$refs.chatBox; // TODO: 滚动条有点小问题

      chatBox.scrollTop = chatBox.scrollHeight - chatBox.style.height;
    },
    sendMessage: function sendMessage() {
      var _this = this;

      if (this.sendContent.trim() === '') return; // TODO: ajax 请求

      this.messages.push({
        type: 'send',
        from: '我',
        time: dayjs().format('YYYY-MM-DD HH:mm:ss'),
        content: this.sendContent
      });
      this.sendContent = '';
      setTimeout(function () {
        _this.messages.push({
          type: 'receive',
          from: '监考老师',
          time: dayjs().format('YYYY-MM-DD HH:mm:ss'),
          content: '好的'
        });
      }, 2000);
    }
  }
};
