import { getExamCardList, getExamRecordList } from '../../api/exam'

export default {
  name: 'ExamCardList',
  data () {
    return {
      description: '您可以随意点击下面的考试卡片开始一场属于您的考试',
      extraImage: 'https://gw.alipayobjects.com/zos/rmsportal/RzwpdLnhmvDJToTdfDPe.png',
      dataSource: [],
      record: {}
    }
  },
  methods: {
    joinExam (id, date, elapse) {
      const newDate = new Date()
      if (Date.parse(date) < newDate && Date.parse(date) + elapse * 60 * 1000 > newDate) {
        for (const ex in this.record) {
          if (id === this.record[ex].examId) {
            this.$notification.error({
              message: '您已参加过本场考试！不可重复参加！'
            })
          } else {
            const routeUrl = this.$router.resolve({
              path: `/exam/${id}`
            })
            window.open(routeUrl.href, '_blank')
          }
        }
      } else {
        this.$notification.error({
          message: '未在考试时间！进入考试失败！'
        })
      }
    }
  },
  mounted () {
    // 从后端数据获取考试列表，适配前端卡片
    getExamCardList().then(res => {
      console.log(res)
      if (res.code === 0) {
        this.dataSource = res.data
      } else {
        this.$notification.error({
          message: '获取考试列表失败',
          description: res.msg
        })
      }
    }).catch(err => {
      // 失败就弹出警告消息
      this.$notification.error({
        message: '获取考试列表失败',
        description: err.message
      })
    })
    getExamRecordList().then(res => {
      console.log(res)
      if (res.code === 0) {
        this.record = res.data
      } else {
        this.$notification.error({
          message: '获取考试记录失败',
          description: res.msg
        })
      }
    }).catch(err => {
      // 失败就弹出警告消息
      this.$notification.error({
        message: '获取考试记录失败',
        description: err.message
      })
    })
  }
}
