/* 调色板的样式，需要在 ExamDetail.vue 中也引入 */
@import './highlighterStyles';
@drawer-width: 200px;
@drawer-height: 250px;

#my-drawer {
  width: @drawer-width;
  height: @drawer-height;
  // border: 1px solid #aaa;
  position: absolute;
  right: 33px;
  top: 50%;
  border-radius: 20px;
  box-shadow: #bbb 0px 0px 10px;
  transform: translate(0, -50%);
  background-color: white;
  z-index: 99;
  display: flex;
  justify-content: space-around;
  align-items: center;
}

.drawerAnim-enter,
.drawerAnim-leave-to {
  // transform: translate(120%, -50%) !important;
  opacity: 0;
}

.drawerAnim-enter-active,
.drawerAnim-leave-active {
  transition: 0.4s;
}

.drawerAnim-enter-to,
.drawerAnim-leave {
  // transform: translate(0, -50%) !important;
  opacity: 1;
}

#closeIcon {
  width: 25px;
  height: 25px;
  position: absolute;
  right: 10px;
  top: 10px;
  cursor: pointer;
}

.container {
  width: 80%;
  height: 80%;
}

#palette {
  display: flex;
  width: 100%;
  height: 60%;
  justify-content: space-between;
  // background: #bbb;
  flex-wrap: wrap;
  .border {
    width: @drawer-width * 0.8 * 0.201;
    height: @drawer-width * 0.8 * 0.201;
    border: 1px solid #ccc;
    border-radius: 50%;
    display: flex;
    align-items: center;
    justify-content: center;
    cursor: pointer;

    div {
      width: 85%;
      height: 85%;
      border-radius: 50%;
    }

    &.active {
      border: 2px solid rgba(70, 181, 225, 0.8);
      box-shadow: 0px 0px 6px rgba(70, 181, 225, 0.8);
    }
  }
}
