#toolNotice {
  position: absolute;
  width: 350px;
  height: 200px;
  top: 50%;
  right: 50px;
  transform: translate(0, -50%);
  background: cadetblue;
  box-shadow: 0 0 5px #999;
  cursor: default;
  border-radius: 5px;

  .container {
    width: 95%;
    height: 82%;
    margin: 0 auto;
    margin-top: 8%;
    background-color: #ffffff;
    overflow: auto;
    list-style: none;
    padding: 0 10px 0 10px;
    border-radius: inherit;

    .noticeFrom {
      font-weight: 800;
    }

    .noticeTime {
      margin-left: 10px;
      color: #aaa;
    }

    p {
      // 如果能选中就被高亮了...
      // user-select: text;
      // cursor: text;
    }
  }
}

#closeIcon {
  width: 20px;
  height: 20px;
  position: absolute;
  right: 5px;
  top: 5px;
  cursor: pointer;
}

.noticeAnim-enter,
.noticeAnim-leave-to {
  opacity: 0;
}

.noticeAnim-enter-active,
.noticeAnim-leave-active {
  transition: 0.2s;
}

.noticeAnim-enter-to,
.noticeAnim-leave {
  opacity: 1;
}
