import { mapActions, mapGetters } from 'vuex'

export default {
  name: 'UserMenu',
  methods: {
    ...mapActions(['Logout']), // 清除token和localStorage中的信息
    ...mapGetters(['nickname', 'avatar']), // 从全局变量中获取用户昵称和头像
    handleLogout () {
      const that = this

      this.$confirm({
        title: '提示',
        content: '真的要注销登录吗 ?',
        onOk () {
          return that.Logout({}).then(() => {
            window.location.reload()
          }).catch(err => {
            that.$message.error({
              title: '错误',
              description: err.message
            })
          })
        },
        onCancel () {
        }
      })
    }
  }
}
